import React, { Component, createContext } from 'react';
import './App.css';


const MyContext = createContext();


class MyProvider extends Component {
  state = {
    name: 'Wes',
    age: 100,
    cool: true,
  }
  render() {
    return (
      <MyContext.Provider value={{
        state: this.state
      }}>
        {this.props.children}
      </MyContext.Provider>
    )
  }
}

const Family = (props) => (
  <div className="family">
    <Person />
  </div>
)


class Person extends Component{
  render(){
    return (
      <MyContext.Consumer>
        {(context) => (
          <p>Age: {context.state}</p>
        )}
      </MyContext.Consumer>
    )
  }
}
class App extends Component {

  render(){
    return (
      <MyProvider>
        <div className="App">
          <Family />
        </div>
      </MyProvider>
    );
  }
}

export default App;
