import React, { Component } from 'react';
import './App.css';
class Person extends Component{
  render(){
    return <p>i am {this.props.name}</p>
  }
}
class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      name: 'Wes',
      age: 100,
      cool: true,
    }
  }
  render(){
    return (
      <div className="App">
        <h1>i am app</h1>
        <Person name={this.state.name} />
      </div>
    );
  }
}

export default App;
